import React from "react";
import { useEffect, useState } from "react";
import bootstrap from "bootstrap/dist/css/bootstrap.min.css";

import trainservice from "../services/trainservice";
//import Pagination1 from "./Pagination1";
import Pagination from "../components/Pagination";
const Trainlist = () => {
  const [train, setTrains] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(2);
  const lastPostIndex = currentPage * postsPerPage;
  const firstPostIndex = lastPostIndex - postsPerPage;
  const currentPosts = train.slice(Ps, lastPostIndex);

  const init = () => {
    trainservice
      .getAll()
      .then((response) => {
        console.log("Printing train data", response.data);
        setTrains(response.data);
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };

  useEffect(() => {
    init();
  }, []);
  return (
    <section className="showcase login">
      <div className="showcase-overlay">
        <div className="container">
          {/* <h3
            style={{ color: "white", fontWeight: "bolder", fontSize: "40px" }}
          >
            List of Trains
          </h3> */}
          <hr />

          <div>
            <table className="table table-bordered table-light table-striped">
              <thead className="thead-light">
                <tr>
                  <th> train_name</th>
                  <th> start_from</th>
                  <th> departure_time</th>
                  <th> to_destination</th>
                  <th> arrival_time</th>
                  <th> price</th>
                </tr>
              </thead>
              <tbody>
                {train.map((trains) => (
                  <tr key={trains.train_no}>
                    <td>{trains.train_name}</td>
                    <td>{trains.start_from}</td>
                    <td>{trains.departure_time}</td>
                    <td>{trains.to_destination}</td>
                    <td>{trains.arrival_time}</td>
                    <td>{trains.price}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
         
          <Pagination
            train={currentPosts}
            totalPosts={train.length}
            postsPerPage={postsPerPage}
            setCurrentPage={setCurrentPage}
            currentPage={currentPage}
          />
        </div>
      </div>
    </section>
  );
};

export default Trainlist;
