import React, { useState, useEffect } from "react";
import auth from "../services/auth.service";
import { Link, useNavigate, useParams } from "react-router-dom";
const Userlogin = () => {
  const [username, setUserName] = useState("");
  const [password, setPass] = useState("");
  const navigate = useNavigate();
  const Regi = (e) => {
    e.preventDefault();

    const val = {
      username,
      password,
    };

    auth
      .auth(val)
      .then((response) => {
        console.log("Feedback added successfully", response.data);
        localStorage.setItem("jwt", response.data.jwt);
        localStorage.setItem("username", val.username);

        window.location.href = "/profile";
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };

  return (
    <div className="Homeregister">
      <div class="container d-flex justify-content-center">
        <div class="row my-2 mx-2">
          <div class="col-md-12">
            <form>
              <div class="form-group pt-2 pl-1">
                <label for="exampleInputName">Your UserName</label>
                <input
                  type="text"
                  class="form-control"
                  id="exampleInputName"
                  value={username}
                  onChange={(e) => setUserName(e.target.value)}
                />
              </div>

              <div class="form-group pl-1">
                <label for="password">Your email Adress</label>
                <input
                  type="password"
                  class="form-control"
                  id="exampleInputPassword"
                  value={password}
                  onChange={(e) => setPass(e.target.value)}
                />
              </div>

              <div class="row homecontactbtn">
                <div class="col-md-3 offset-md-9">
                  <button
                    class="btn btn-primary homecontactbtn1"
                    onClick={(e) => {
                      Regi(e);
                    }}
                  >
                    Login
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Userlogin;
