import React, { useState, useEffect } from "react";
import auth from "../services/auth.service";
import { Link, useNavigate, useParams } from "react-router-dom";
const Userlogin = () => {
  const [username, setUserName] = useState("");
  const [password, setPass] = useState("");
  const navigate = useNavigate();
  const Regi = (e) => {
    e.preventDefault();

    const val = {
      username,
      password,
    };

    auth
      .auth(val)
      .then((response) => {
        console.log("Feedback added successfully", response.data);
        localStorage.setItem("jwt", response.data.jwt);
        localStorage.setItem("username", val.username);

        window.location.href = "/profile";
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };

  return (
    <section className="showcase login">
      <div className="showcase-overlay">
        {/* <div className="Homeregister">
          <div class="container d-flex justify-content-center">
            <div class="row my-2 mx-2">
              <div class="col-md-12"> */}
        <form className="form-control">
          {/* <div class="form-group pt-2 pl-1">
                    <label for="exampleInputName">Your UserName</label> */}
          <input
            type="text"
            //class="form-control"
            name="username"
            id="exampleInputName"
            placeholder="Enter your name"
            value={username}
            onChange={(e) => setUserName(e.target.value)}
          />
          {/* </div> */}

          {/* <div className="form-control">
                <label for="password">Your Email Address</label> */}
          <input
            type="password"
            //class="form-control"
            name="password"
            id="exampleInputPassword"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPass(e.target.value)}
          />
          {/* </div> */}

          {/* <div className="form-control"> */}
          {/* <div class="col-md-3 offset-md-9"> */}
          <button
            class="btn btn-primary homecontactbtn1"
            onClick={(e) => {
              Regi(e);
            }}
          >
            Login
          </button>
          {/* </div> */}
          {/* </div> */}
        </form>
        {/* </div> */}
        {/* </div>
          </div>
        </div> */}
      </div>
    </section>
  );
};
export default Userlogin;
