              <br></br>
              <input
                type="text"
                name="name"
                id="name"
                placeholder="Your Name"
                onChange={(e) => setName(e.target.value)}
                required
              />
              <input
                type="email"
                name="email"
                id="email"
                placeholder="Your email address"
                required
              />
              <input
                type="password"
                name="password"
                id="password"
                placeholder="Your password"
              />
              <button type="submit">Log In</button>
            </form>
            <div>
              <button
                className="openModalbtn"
                onClick={(o) => saveUser(o)}
                // onClick={() => {
                //   setModalOpen(true);
                // }}
                variant="success"
                type="submit"
                id="submit"
              >
                <Link to="/Modal">SUBMIT</Link>
              </button>
              {modalOpen && <Modal setOpenModal={setModalOpen} />}
            </div>
