import { render } from "@testing-library/react";
import React from "react";
import user1 from "../assets/user1.png";
import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import userservice from "../services/userservice";

const SignUp = () => {
  const [username, setName] = useState("");
  const [emailId, setEmail] = useState("");

  const [password, setPass] = useState("");
  const [age, setAge] = useState("");

  const [gender, setGender] = useState("");
  const [ph_no, setPhone] = useState("");
  const [roles, setRoles] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const saveUser = (e) => {
    e.preventDefault();

    const user = { username, emailId, password, age, gender, ph_no, roles, id };

    if (id) {
      //update
      userservice
        .update(user)
        .then((response) => {
          console.log("user data updated successfully", response.data);
          navigate("/user");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      userservice
        .create(user)
        .then((response) => {
          console.log("user added successfully", response.data);
          navigate("/signup");
          //alert("Congratulations!! Data successfully inserted!!");

        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };
  useEffect(() => {
    if (id) {
      userservice
        .get(id)
        .then((user) => {
          setName(user.data.username);
          setEmail(user.data.emailId);
          setPass(user.data.password);
          setAge(user.data.age);
          setGender(user.data.gender);
          setPhone(user.data.ph_no);
          setRoles(user.data.roles);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }

  }, []);


   let popup = document.getElementById("popup");

   function openPopup() {
     popup.classList.add("open-popup");
   }
   function closePopup() {
     popup.classList.remove("open-popup");
   }
   render();
  {
    const mystyle = {
      height: "80px",
      margintop: "20px",
      color: "white"
    }; 

    return (
      <section className="showcase login">
        <div className="showcase-overlay">
          <form className="form-control">
            <img src={user1} style={mystyle} />
            <br></br>
            <input
              type="text"
              name="username"
              id="username"
              placeholder="Username"
              value={username}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <input
              type="email"
              name="email"
              id="email"
              placeholder="Your email address"
              value={emailId}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              name="password"
              id="password"
              placeholder="Choose your password"
              value={password}
              onChange={(e) => setPass(e.target.value)}
            />
            {/* <input
                type="password"
                name="password2"
                id="password2"
                placeholder="Confirm your password"
                value={password}
                onChange={(e) => setPass(e.target.value)}
              /> */}
            <input
              type="number"
              name="age"
              id="age"
              placeholder="Choose your Age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
            />
            <input
              type="text"
              name="gender"
              id="gender"
              placeholder="Choose your gender"
              value={gender}
              onChange={(e) => setGender(e.target.value)}
            />
            <input
              type="number"
              name="mobile"
              id="mobile"
              placeholder="Enter your Mobile Number"
              value={ph_no}
              onChange={(e) => setPhone(e.target.value)}
            />
            <input
              type="text"
              name="user"
              id="user"
              placeholder="User/Admin"
              value={roles}
              onChange={(e) => setRoles(e.target.value)}
            />
            {/* <a href="http://127.0.0.1:5500/src/components/popup.html"> */}
            {/* <button
                className="li"
                type="submit"
                onClick={(o) => saveUser(o)}
                onClick={() => openPopup()}
              >
                Create Your Account
              </button> */}
            {/* </a> */}
            <a href="./components/popup.html">
              <button
                className="li"
                type="submit"
                onClick={(o) => saveUser(o)}

              >
                Create Your Account
              </button>
            </a>
            <div class="popup" id="popup">
              {/* <img src="tick.jpg"> */}
              <h2>Thank You!</h2>
              <p>Your details has been successfully submitted. Thanks!</p>
              <button type="button">OK</button>
            </div>
          </form>
        </div>
      </section>
    );    
    }    
};
export default SignUp;
